package com.example.petShopBackend.products;


import org.springframework.boot.CommandLineRunner;
import org.springframework.context.annotation.Bean;
import org.springframework.context.annotation.Configuration;

import java.util.List;

@Configuration
public class ProductConfig {

    @Bean
    CommandLineRunner productInitialData(ProductRepository productRepository) {
        return args -> {
            Product dogFood = new Product(
                    "Dry Dog Food 2kg",
                    "Nutritious food for your little friends",
                    37.99,
                    1000,
                    4.87,
                    "/products/products_dry_dog_food.png"
            );

            Product catFood = new Product(
                    "Cat Food 2kg",
                    "Nutritious food for your little cat friends",
                    24.99,
                    1000,
                    4.9,
                    "data:image/jpeg;base64,"
            );


            Product catHouse = new Product(
                    "Cat Tree House",
                    "A cozy home for your cats.",
                    369.99,
                    20,
                    4.45,
                    "/products/products_cat_house.jpg"
            );

            Product dogToy1 = new Product(
                    "Dog Toy",
                    "A toy for your dogs",
                    24.89,
                    157,
                    4.87,
                    "/products/products_toy1.png"
            );


            Product catToy = new Product(
                    "Cat Toy Mouse",
                    "Cat Toy for an active pet.",
                    57.99,
                    56,
                    4.32,
                    "https://headsupfortails.com/cdn/shop/articles/Cat_playing_with_toy.jpg?v=1644820121"
            );


            productRepository.saveAll(List.of(dogFood, catFood, catHouse, dogToy1, catToy));
        };
    }

}
